INSERT INTO `#@__site_singellist` (`type`, `title`, `body`, `pubdate`) VALUES ('agree', '求职安全防范指南', '<p><br/></p><p style=\"box-sizing: border-box; margin-top: 1.12em; margin-bottom: 1.12em; min-height: 1em; font-size: initial; color: rgba(0, 0, 0, 0.65); white-space: normal; line-height: 1.8; font-family: -apple-system, system-ui, &quot;Segoe UI&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif, &quot;Apple Color Emoji&quot;, &quot;Segoe UI Emoji&quot;, &quot;Segoe UI Symbol&quot;; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; color: rgb(22, 145, 121); font-size: 18px;\">安全小课堂，带你了解安全防范知识。</span></p><p style=\"box-sizing: border-box; margin-top: 1.12em; margin-bottom: 1.12em; min-height: 1em; font-size: initial; color: rgba(0, 0, 0, 0.65); white-space: normal; line-height: 1.8; font-family: -apple-system, system-ui, &quot;Segoe UI&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif, &quot;Apple Color Emoji&quot;, &quot;Segoe UI Emoji&quot;, &quot;Segoe UI Symbol&quot;; background-color: rgb(255, 255, 255);\">&nbsp;</p><h2 style=\"box-sizing: border-box; margin: 0.75em 0px; color: rgba(0, 0, 0, 0.85); font-weight: bolder; white-space: normal; font-family: -apple-system, system-ui, &quot;Segoe UI&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif, &quot;Apple Color Emoji&quot;, &quot;Segoe UI Emoji&quot;, &quot;Segoe UI Symbol&quot;; background-color: rgb(255, 255, 255);\">一、六处必看，提高警惕</h2><ol style=\"box-sizing: border-box; margin-top: 1.12em; margin-bottom: 1.12em; color: rgba(0, 0, 0, 0.65); white-space: normal; font-family: -apple-system, system-ui, &quot;Segoe UI&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&q