const inputData = $input.first().json;
const maxRetries = 3;
const baseDelay = 1000;

const currentRetry = $execution.customData?.notionRetryCount || 0;

function calculateDelay(retryCount) {
  return baseDelay * Math.pow(2, retryCount);
}

function validateNotionData(data) {
  const errors = [];

  if (!data.title || data.title.trim().length === 0) {
    errors.push('æ ‡é¢˜ä¸èƒ½ä¸ºç©º');
  }

  if (!data.content || data.content.trim().length === 0) {
    errors.push('å†…å®¹ä¸èƒ½ä¸ºç©º');
  }

  if (data.title && data.title.length > 60) {
    errors.push(`æ ‡é¢˜è¿‡é•¿: ${data.title.length}/60`);
  }

  return errors;
}

function sanitizeNotionData(data) {
  const sanitized = { ...data };

  if (sanitized.title) {
    sanitized.title = sanitized.title.trim().substring(0, 60);
  }

  if (sanitized.summary) {
    sanitized.summary = sanitized.summary.trim().substring(0, 255);
  }

  if (sanitized.author) {
    sanitized.author = sanitized.author.trim().substring(0, 20);
  }

  if (sanitized.source) {
    sanitized.source = sanitized.source.trim().substring(0, 30);
  }

  sanitized.categoryId = parseInt(sanitized.categoryId || sanitized.typeid || 1);
  sanitized.quality_score = parseFloat(sanitized.quality_score || sanitized.qualityScore || 0);
  sanitized.weight = parseInt(sanitized.weight || 1);
  sanitized.cityid = parseInt(sanitized.cityid || 1);

  if (!sanitized.publishedAt) {
    sanitized.publishedAt = new Date().toISOString();
  }

  sanitized.processingTime = Date.now() - (sanitized.startTime || Date.now());

  return sanitized;
}

try {
  const validationErrors = validateNotionData(inputData);
  if (validationErrors.length > 0) {
    throw new Error(`æ•°æ®éªŒè¯å¤±è´¥: ${validationErrors.join(', ')}`);
  }

  const sanitizedData = sanitizeNotionData(inputData);

  const notionData = {
    ...sanitizedData,
    storageAttempt: currentRetry + 1,
    storageTimestamp: new Date().toISOString(),
    executionId: $execution.id,
    runIndex: $runIndex
  };

  console.log(`å‡†å¤‡å­˜å‚¨åˆ°Notion (å°è¯• ${currentRetry + 1}/${maxRetries}):`, {
    title: notionData.title,
    source: notionData.source,
    categoryId: notionData.categoryId
  });

  return {
    json: notionData
  };

} catch (error) {
  console.error(`Notionå­˜å‚¨å‡†å¤‡å¤±è´¥ (å°è¯• ${currentRetry + 1}/${maxRetries}):`, error.message);

  if (currentRetry < maxRetries - 1) {
    const delay = calculateDelay(currentRetry);
    console.log(`å°†åœ¨ ${delay}ms åé‡è¯•...`);

    $execution.customData = $execution.customData || {};
    $execution.customData.notionRetryCount = currentRetry + 1;

    return {
      json: {
        ...inputData,
        retryAttempt: currentRetry + 1,
        retryDelay: delay,
        lastError: error.message
      }
    };
  } else {
    throw new Error(`Notionå­˜å‚¨æœ€ç»ˆå¤±è´¥ (${maxRetries}æ¬¡å°è¯•): ${error.message}`);
  }
}

const notionResponse = $input.first().json;
const originalData = $input.first().json.originalData || {};

const isSuccess = notionResponse && notionResponse.id;
const notionPageId = notionResponse.id;
const notionUrl = notionResponse.url;

const statusRecord = {
  executionId: $execution.id,
  runIndex: $runIndex,
  timestamp: new Date().toISOString(),

  storageStatus: isSuccess ? 'success' : 'failed',
  notionPageId: notionPageId || null,
  notionUrl: notionUrl || null,

  contentInfo: {
    title: originalData.title || notionResponse.properties?.['æ ‡é¢˜']?.title?.[0]?.plain_text || 'Unknown',
    source: originalData.source || notionResponse.properties?.['æ¥æº']?.select?.name || 'Unknown',
    category: originalData.category || notionResponse.properties?.['åˆ†ç±»åç§°']?.select?.name || 'Unknown',
    qualityScore: originalData.quality_score || 0
  },

  performance: {
    processingTime: originalData.processingTime || 0,
    storageAttempts: originalData.storageAttempt || 1,
    totalTime: Date.now() - (originalData.startTime || Date.now())
  },

  error: isSuccess ? null : {
    message: notionResponse.error || 'Unknown error',
    code: notionResponse.code || 'UNKNOWN',
    details: notionResponse.details || null
  }
};

if (isSuccess) {
  console.log('âœ… Notionå­˜å‚¨æˆåŠŸ:', {
    pageId: notionPageId,
    title: statusRecord.contentInfo.title,
    source: statusRecord.contentInfo.source,
    attempts: statusRecord.performance.storageAttempts,
    totalTime: statusRecord.performance.totalTime + 'ms'
  });
} else {
  console.error('âŒ Notionå­˜å‚¨å¤±è´¥:', {
    title: statusRecord.contentInfo.title,
    error: statusRecord.error?.message,
    attempts: statusRecord.performance.storageAttempts
  });
}

const outputData = {
  storageSuccess: isSuccess,
  notionPageId: notionPageId,
  notionUrl: notionUrl,

  ...originalData,

  notion: {
    pageId: notionPageId,
    url: notionUrl,
    storedAt: new Date().toISOString(),
    attempts: statusRecord.performance.storageAttempts
  },

  statusRecord: statusRecord,

  processing_status: isSuccess ? 'stored' : 'storage_failed'
};

if (isSuccess) {
  outputData.readyForPublish = true;
  outputData.publishData = {
    service: 'article',
    action: 'put',
    title: originalData.title?.substring(0, 60) || '',
    typeid: originalData.categoryId || 1,
    body: originalData.content || '',
    writer: originalData.author || 'AIé‡‡é›†',
    source: originalData.source || 'AIé‡‡é›†',
    sourceurl: originalData.source_url || '',
    keywords: typeof originalData.keywords === 'string' 
      ? originalData.keywords.substring(0, 50)
      : (Array.isArray(originalData.keywords) 
          ? originalData.keywords.join(',').substring(0, 50)
          : ''),
    description: originalData.summary?.substring(0, 255) || '',
    litpic: originalData.image_url || ''
  };
}

return {
  json: outputData
};

const errorData = $input.first().json;
const error = errorData.error || 'Unknown error';

function categorizeError(errorMessage) {
  const errorMsg = errorMessage.toLowerCase();

  if (errorMsg.includes('unauthorized') || errorMsg.includes('invalid token')) {
    return {
      type: 'authentication',
      severity: 'high',
      recoverable: false,
      action: 'check_credentials'
    };
  }

  if (errorMsg.includes('rate limit') || errorMsg.includes('too many requests')) {
    return {
      type: 'rate_limit',
      severity: 'medium',
      recoverable: true,
      action: 'retry_with_delay'
    };
  }

  if (errorMsg.includes('database') || errorMsg.includes('not found')) {
    return {
      type: 'database_error',
      severity: 'high',
      recoverable: false,
      action: 'check_database_config'
    };
  }

  return {
    type: 'unknown',
    severity: 'medium',
    recoverable: true,
    action: 'retry'
  };
}

const errorCategory = categorizeError(error);
const errorReport = {
  timestamp: new Date().toISOString(),
  executionId: $execution.id,
  runIndex: $runIndex,

  error: {
    message: error,
    category: errorCategory,
    originalData: errorData.originalData || {}
  },

  contentInfo: {
    title: errorData.originalData?.title || 'Unknown',
    source: errorData.originalData?.source || 'Unknown',
    category: errorData.originalData?.category || 'Unknown'
  },

  recovery: {
    recommended: errorCategory.action,
    canRetry: errorCategory.recoverable,
    severity: errorCategory.severity
  }
};

console.error('ğŸš¨ Notionå­˜å‚¨é”™è¯¯:', {
  title: errorReport.contentInfo.title,
  error: error,
  category: errorCategory.type,
  severity: errorCategory.severity,
  recoverable: errorCategory.recoverable
});

let shouldContinueWorkflow = false;
let alternativeAction = null;

switch (errorCategory.type) {
  case 'validation_error':
    shouldContinueWorkflow = true;
    alternativeAction = 'fix_and_retry';
    break;

  case 'rate_limit':
    shouldContinueWorkflow = true;
    alternativeAction = 'delay_and_retry';
    break;

  case 'network_error':
    shouldContinueWorkflow = true;
    alternativeAction = 'retry';
    break;

  case 'authentication':
  case 'database_error':
    shouldContinueWorkflow = false;
    alternativeAction = 'manual_intervention';
    break;

  default:
    shouldContinueWorkflow = true;
    alternativeAction = 'continue_with_warning';
}

const outputData = {
  errorHandled: true,
  shouldContinue: shouldContinueWorkflow,
  alternativeAction: alternativeAction,

  errorReport: errorReport,

  originalData: errorData.originalData || {},

  processing_status: 'storage_failed'
};

if (shouldContinueWorkflow) {
  outputData.storageSuccess = false;
  outputData.continueWorkflow = true;
  outputData.readyForPublish = true;
  outputData.publishData = {
    service: 'article',
    action: 'put',
    title: (errorData.originalData?.title || '').substring(0, 60),
    typeid: errorData.originalData?.categoryId || 1,
    body: errorData.originalData?.content || '',
    writer: errorData.originalData?.author || 'AIé‡‡é›†',
    source: errorData.originalData?.source || 'AIé‡‡é›†'
  };

  outputData.notionStorageError = error;
  outputData.notionStorageFailed = true;
}

if (errorCategory.severity === 'high') {
  console.error('ğŸš¨ ä¸¥é‡é”™è¯¯éœ€è¦äººå·¥å¹²é¢„:', {
    error: error,
    category: errorCategory.type,
    contentTitle: errorReport.contentInfo.title
  });
}

return {
  json: outputData
};
