INSERT INTO `#@__site_notify` (`title`, `email_state`, `email_title`, `email_body`, `sms_state`, `sms_tempid`, `sms_intempid`, `sms_body`, `sms_note`, `sms_rule`, `wechat_state`, `wechat_tempid`, `wechat_body`, `wechat_serial`, `site_state`, `site_title`, `site_body`, `app_state`, `app_title`, `app_body`, `state`, `system`) VALUES ('会员-订单退款通知', '1', '订单退款通知-\$webname', 'HI，\$username<br /><br />您的订单：\$order买家申请退款<br />订单金额：\$amount<br />退款原因：\$info<br /><a href=\"\$url\" target=\"_blank\">点击查看</a>。<br /><br />\$webname<br />\$time<br /><br />这是一封系统自动发出的邮件，请不要直接回复。', '1', 'SMS_473755165', '', '您的订单\$order买家申请退款，订单金额\$amount，请进入商家会员中心查看。', '使用场景：会员申请退款通知商家；完整示例：您的订单21A1581766416375买家申请退款，订单金额10，请进入商家会员中心查看。', '{\"order\": \"other_number2\", \"amount\": \"money\"}', '1', 'e2mfdtAjCDkKpXXn2dP15Cj-nRmZr17-WeDXLXzPevY', '{\r\n    \"first\":{\"value\":\"订单：\$order申请退款\"},\r\n    \"reason\":{\"value\":\"\$note\"},\r\n    \"refund\":{\"value\":\"\$amount\"},\r\n    \"remark\":{\"value\":\"请进入会员中心查看详细信息。\"}\r\n}', 'TM00004', '1', '订单\$order申请退款', '您的订单：\$order买家申请退款<br />订单金额：\$amount<br />退款原因：\$info<br /><a href=\"\$url\" target=\"_blank\">点击查看</a>。', '1', '订单\$order申请退款', '您的订单：\$order买家申请退款，订单金额：\$amount，退款原因：\$info', '0', '1');
