INSERT INTO `#@__site_notify` (`title`, `email_state`, `email_title`, `email_body`, `sms_state`, `sms_tempid`, `sms_intempid`, `sms_body`, `sms_note`, `sms_rule`, `wechat_state`, `wechat_tempid`, `wechat_body`, `wechat_serial`, `site_state`, `site_title`, `site_body`, `app_state`, `app_title`, `app_body`, `state`, `system`) VALUES ('会员-订单退款成功', '1', '订单\$order退款成功-\$webname', 'HI，\$username<br /><br />您的订单：\$order退款申请成功，请进入会员中心查看，<a href=\"\$url\" target=\"_blank\">点击查看</a>。<br /><br />\$webname<br />\$time<br /><br />这是一封系统自动发出的邮件，请不要直接回复。', '1', 'SMS_473895168', '', '订单\$order申请退款成功，请进入会员中心查看。', '使用场景：退款申请成功通知提醒会员买家；完整示例：订单21A1581766416375申请退款成功，请进入会员中心查看。', '{\"order\": \"other_number2\"}', '1', '-XpaXpZ1jLYGyIKIU8lcULv1zDxjhPT5SZw3f2mcTik', '{\r\n    \"first\":{\"value\":\"订单：\$order\"},\r\n    \"keyword1\":{\"value\":\"退款成功\"},\r\n    \"keyword2\":{\"value\":\"\$amount\"},\r\n    \"keyword3\":{\"value\":\"审核通过\"},\r\n    \"remark\":{\"value\":\"请进入会员中心查看详细信息。\"}\r\n}', 'OPENTM202735558', '1', '订单\$order退款申请成功', '您的订单：\$order退款申请成功，<a href=\"\$url\" target=\"_blank\">点击查看</a>', '1', '订单\$order退款申请成功', '您的订单：\$order退款申请成功，点击查看', '0', '1');
