INSERT INTO `#@__site_notify` (`title`, `email_state`, `email_title`, `email_body`, `sms_state`, `sms_tempid`, `sms_intempid`, `sms_body`, `sms_note`, `sms_rule`, `wechat_state`, `wechat_tempid`, `wechat_body`, `wechat_serial`, `site_state`, `site_title`, `site_body`, `app_state`, `app_title`, `app_body`, `state`, `system`) VALUES ('会员-新评论通知', '1', '新评论通知-\$webname', 'HI，\$username<br /><br />您发布的\$title，有新的评论，<a href=\"\$url\" target=\"_blank\">点击查看</a>。<br /><br />\$webname<br />\$time<br /><br />这是一封系统自动发出的邮件，请不要直接回复。', '1', 'SMS_473650159', '', '\$username您好，您的信息（\$title）有新的评论，请进入会员中心查看！', '使用场景：有新评论时通知提醒信息发者；完整示例：空空当当您好，您的信息（二环路中段住房出租）有新的评论，请进入会员中心查看！', '{\"username\": \"user_nick\", \"title\": \"others\"}', '1', 'HHzoXVKSgcGEUZaneW6mEIc3BA2hRYKkG-1G3FgHyw0', '{\r\n    \"first\":{\"value\":\"新评论通知\"},\r\n    \"keyword1\":{\"value\":\"\$title\"},\r\n    \"keyword2\":{\"value\":\"\$date\"},\r\n    \"keyword3\":{\"value\":\"点击查看\"},\r\n    \"remark\":{\"value\":\"\"}\r\n}', 'OPENTM403071472', '0', '\$title有新的评论', '\$title有新的评论，<a href=\"\$url\" target=\"_blank\">点击查看！</a>', '1', '\$title有新的评论', '\$title有新的评论，点击查看！', '0', '1');
