INSERT INTO `#@__site_notify` (`title`, `email_state`, `email_title`, `email_body`, `sms_state`, `sms_tempid`, `sms_intempid`, `sms_body`, `sms_note`, `sms_rule`, `wechat_state`, `wechat_tempid`, `wechat_body`, `wechat_serial`, `site_state`, `site_title`, `site_body`, `app_state`, `app_title`, `app_body`, `state`, `system`) VALUES ('活动-报名成功通知', '1', '活动报名成功', '您参与的活动报名成功，请在活动时间内前往参与：<br /><br />\r\n\r\n活动名称：\$title<br />\r\n活动时间：\$times<br />\r\n活动地点：\$address<br />\r\n\r\n<a href=\"\$url\">点击查看订单详情</a>', '1', 'SMS_473715147', '', '您参与的活动（\$title）报名成功，请在活动时间（\$began - \$end）内前往参与。', '使用场景：会员报名成功后，短信通知用户；完整示例：您参与的活动（华三路由交换新产品交流会）报名成功，请在活动时间（2024-09-23 10:00:00 - 2024-09-23 18:00:00）内前往参与。', '{\"title\": \"others\", \"began\": \"time\", \"end\": \"time\"}', '1', 'HHzoXVKSgcGEUZaneW6mEIc3BA2hRYKkG-1G3FgHyw0', '{\r\n    \"first\":{\"value\":\"活动报名成功\"},\r\n    \"keyword1\":{\"value\":\"\$title\"},\r\n    \"keyword2\":{\"value\":\"\$times\"},\r\n    \"keyword3\":{\"value\":\"地点：\$address\"},\r\n    \"remark\":{\"value\":\"请在活动时间内前往参与~\"}\r\n}', 'OPENTM403071472', '1', '活动报名成功', '您参与的活动（\$title）报名成功，请在活动时间内前往参与，点击<a href=\"\$url\" target=\"_blank\">查看订单详情</a>', '1', '活动报名成功', '您参与的活动（\$title）报名成功，请在活动时间内前往参与，点击查看订单详情', '0', '1');
INSERT INTO `#@__site_notify` (`title`, `email_state`, `email_title`, `email_body`, `sms_state`, `sms_tempid`, `sms_intempid`, `sms_body`, `sms_note`, `sms_rule`, `wechat_state`, `wechat_tempid`, `wechat_body`, `wechat_serial`, `site_state`, `site_title`, `site_body`, `app_state`, `app_title`, `app_body`, `state`, `system`) VALUES ('活动-有新的报名通知', '1', '有新的活动报名通知', '您活动有新的报名通知：\r\n\r\n活动名称：\$title<br />\r\n活动时间：\$times<br />\r\n活动地点：\$address<br />\r\n报名人：\$user<br />\r\n\r\n<a href=\"\$url\">点击查看详情</a>', '1', 'SMS_473780174', '', '您的活动（\$title）有新的报名通知，请进入会员中心查看。', '使用场景：活动报名后，短信通知主办方；完整示例：您的活动（华三路由交换新产品交流会）有新的报名通知，请进入会员中心查看。', '{\"title\": \"others\"}', '1', 'HHzoXVKSgcGEUZaneW6mEIc3BA2hRYKkG-1G3FgHyw0', '{\r\n    \"first\":{\"value\":\"活动报名通知\"},\r\n    \"keyword1\":{\"value\":\"\$title\"},\r\n    \"keyword2\":{\"value\":\"\$times\"},\r\n    \"keyword3\":{\"value\":\"报名人：\$user\"},\r\n    \"remark\":{\"value\":\"点击进入活动管理中心查看\"}\r\n}', 'OPENTM403071472', '1', '有新的活动报名通知', '您的活动（\$title）有新的报名通知，点击<a href=\"\$url\" target=\"_blank\">查看详情</a>', '1', '有新的活动报名通知', '您的活动（\$title）有新的报名通知，点击<a href=\"\$url\" target=\"_blank\">查看详情</a>', '0', '1');
