INSERT INTO `#@__site_notify` (`title`, `email_state`, `email_title`, `email_body`, `sms_state`, `sms_tempid`, `sms_intempid`, `sms_body`, `sms_note`, `sms_rule`, `wechat_state`, `wechat_tempid`, `wechat_body`, `wechat_serial`, `site_state`, `site_title`, `site_body`, `app_state`, `app_title`, `app_body`, `state`, `system`) VALUES ('会员-保障金变动通知', '1', '帐户保障金变动提醒-\$webname', '\$username<br /><br />您的帐户有新的保障金变动。<br />变动时间：\$date<br />变动金额：\$amount<br />保障金余额：\$promotion<br />变动说明：\$info<br /><br />\$webname<br />\$time<br /><br />这是一封系统自动发出的邮件，请不要直接回复。', '1', 'SMS_473765161', '', '\$username您好，您的帐户有新的保障金变动，变动金额：\$amount，保障金余额：\$promotion。', '使用场景：会员帐户保障金变动通知提醒；完整示例：空空当当您好，您的帐户有新的保障金变动，变动金额：500，保障金余额：2000。', '{\"username\": \"user_nick\", \"amount\": \"money\", \"promotion\": \"money\"}', '1', 'ERc-9Y-EC7sfM66KVJ79dCjz1zOqTCB0MC0SfECnFWs', '{\r\n    \"first\":{\"value\":\"您的账户有新的保障金变动\"},\r\n    \"keyword1\":{\"value\":\"保障金\"},\r\n    \"keyword2\":{\"value\":\"\$amount\"},\r\n    \"keyword3\":{\"value\":\"\$date\"},\r\n    \"keyword4\":{\"value\":\"\$promotion\"},\r\n    \"remark\":{\"value\":\"\$info\"}\r\n}', 'OPENTM415437054', '1', '您的帐户有新的保障金变动', '您的帐户有新的保障金变动。', '1', '您的帐户有新的保障金变动', '点击查看详细信息！', '0', '1');
INSERT INTO `#@__site_notify` (`title`, `email_state`, `email_title`, `email_body`, `sms_state`, `sms_tempid`, `sms_intempid`, `sms_body`, `sms_note`, `sms_rule`, `wechat_state`, `wechat_tempid`, `wechat_body`, `wechat_serial`, `site_state`, `site_title`, `site_body`, `app_state`, `app_title`, `app_body`, `state`, `system`) VALUES ('会员-提取保障金审核通过', '1', '提取保障金成功-\$webname', 'HI，\$username<br /><br />您的提取保障金申请已审核通过<br /><br />\$webname<br />\$time<br /><br />这是一封系统自动发出的邮件，请不要直接回复。', '1', 'SMS_473805167', '', '\$username您好，您的提取保障金申请已审核通过，请进入会员中心查看。', '使用场景：会员申请提取保障金审核结果通知；完整示例：空空当当您好，您的提取保障金申请已审核通过，请进入会员中心查看。', '{\"username\": \"user_nick\"}', '1', 'mJmCH0W3TNgl09olGnMcjfvSDmvSdaB0aU3wl6wCJus', '{\r\n    \"first\":{\"value\":\"提取保障金申请\"},\r\n    \"keyword1\":{\"value\":\"\$amount\"},\r\n    \"keyword2\":{\"value\":\"\$date\"},\r\n    \"keyword3\":{\"value\":\"审核通过\", \"color\": \"#44b549\"},\r\n    \"remark\":{\"value\":\"提取金额已自动转入您的账户余额，您可以进入会员中心申请提现\", \"color\": \"#ff0000\"}\r\n}', 'OPENTM401873794', '1', '您的提取保障金申请审核通过', '您的提取保障金申请审核通过。', '1', '您的提取保障金申请审核通过', '点击查看', '0', '1');
INSERT INTO `#@__site_notify` (`title`, `email_state`, `email_title`, `email_body`, `sms_state`, `sms_tempid`, `sms_intempid`, `sms_body`, `sms_note`, `sms_rule`, `wechat_state`, `wechat_tempid`, `wechat_body`, `wechat_serial`, `site_state`, `site_title`, `site_body`, `app_state`, `app_title`, `app_body`, `state`, `system`) VALUES ('会员-提取保障金审核失败', '1', '提取保障金审核失败-\$webname', 'HI，\$username<br /><br />您的提取保障金申请审核失败：\$info<br /><br />\$webname<br />\$time<br /><br />这是一封系统自动发出的邮件，请不要直接回复。', '1', 'SMS_473790160', '', '\$username您好，您的提取保障金申请审核失败，请进入会员中心查看原因。', '使用场景：会员申请提取保障金审核结果通知；完整示例：空空当当您好，您的提取保障金申请审核失败，请进入会员中心查看原因。', '{\"username\": \"user_nick\"}', '1', 'mJmCH0W3TNgl09olGnMcjfvSDmvSdaB0aU3wl6wCJus', '{\r\n    \"first\":{\"value\":\"提取保障金申请\"},\r\n    \"keyword1\":{\"value\":\"\$amount\"},\r\n    \"keyword2\":{\"value\":\"\$date\"},\r\n    \"keyword3\":{\"value\":\"审核失败\", \"color\": \"#ff0000\"},\r\n    \"remark\":{\"value\":\"\$info\", \"color\": \"#ff0000\"}\r\n}', 'OPENTM401873794', '1', '您的提取保障金申请审核失败', '您的提取保障金申请审核失败。', '1', '您的提取保障金申请审核失败', '点击查看详情', '0', '1');
